import { createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";
const initialState = {
  cartItems: [],
  cartTotalQuantity: 0,
  cartTotalAmount: 0
};
const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addToCart(state, action) {
      const itemIndex = state.cartItems.findIndex(item => item.id === action.payload.id);
      if (itemIndex >= 0) {
        state.cartItems[itemIndex].cartQuantity += 1;
        toast.info(`increased ${state.cartItems[itemIndex].name} product quantity`, {
          position: "bottom-left"
        });
      } else {
        const tempProductItem = {
          ...action.payload,
          cartQuantity: 1
        };
        state.cartItems.push(tempProductItem);
        toast.success(` ${action.payload.name} add new to cart`, {
          position: "bottom-left"
        });
      }
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    remoFromCart(state, action) {
      const nexTcartItms = state.cartItems.filter(cartItem => cartItem.id !== action.payload.id);
      state.cartItems = nexTcartItms;
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
      toast.error(` ${action.payload.name} removed from cart`, {
        position: "bottom-left"
      });
    }
  }
});
export const {
  addToCart,
  remoFromCart
} = cartSlice.actions;
export default cartSlice.reducer;

